import Head from 'next/head'
import styled from 'styled-components'
import ThemeContext from '../contexts/ThemeContext'
import { Graph, Tree } from '../components/sections'
import {useState, useEffect} from 'react'
import LinkedList from '../components/linked-list'

const DkModeToggle = styled.button`
  background-color: ${(props) => props.theme.textColor};
  color: ${(props) => props.dark ? "#03254c;" : "#fdfcfc;"};
  font-size: 1em;
  margin: 1em;
  padding: .25em 1em;
  // border: 2px solid palevioletred;
  border: none;
  border-radius: 3px;
  outline: none;

`
const Main = styled.div`
  position: fixed;
  width: 100%;
  height: 100%;
  bottom: 0;
  background-color: ${(props) => props.theme.backgroundColor}

`
const Title = styled.h1`
  display: flex;
  flex-direction: row;
  justify-content: center;
  color: ${(props) => props.theme.textColor};
`
const Sections_container = styled.div`
  position: absolute;
  // align-items: center;
  width: 100%;
  height: 100%;
`

export default function Home() {

  const [dark, setDark] = useState(false)

  useEffect(() => {
    console.log('useEffect')
  },[dark])

  return (
    <ThemeContext dark={dark}>
      <Main>
        <DkModeToggle dark={dark} onClick={() => {setDark(!dark)}}>
        {dark ? "LIGHT" : "DARK"}
        </DkModeToggle>
        <Title>Algorithm Visualizer</Title>
        <Sections_container>
          <LinkedList/>
        </Sections_container>
      </Main>

    </ThemeContext>
  )
}
